import React from 'react';
import ReactDOM from 'react-dom';

var currenthours = new Date();
currenthours = currenthours.getHours();

var currentminuts = new Date();
currentminuts = currentminuts.getMinutes();

if(currentminuts >=0 && currentminuts <=9) {
  currentminuts = "0"+currentminuts;
}

var greetings = "";
const greetingscss = {
};

if(currenthours >= 0 && currenthours < 12) {
  greetings = "Good Morning";
  greetingscss.color = "green";
}
else if (currenthours >= 12 && currenthours < 17) {
  greetings = "Good Afternoon";
  greetingscss.color = "orange";
}
else {
  greetings = "Good Evening";
  greetingscss.color = "blue";
}



ReactDOM.render(
  <>
    <div class="box">
      <h1>{`${currenthours}:${currentminuts}`}</h1>
      <h2>Hello, <span style={greetingscss}>{greetings}</span></h2>
    </div>
  </>,
  document.getElementById("root")
);
